import React from 'react';
import { GridLayout } from 'react-grid-layout';
import 'react-grid-layout/css/styles.css';
import 'react-resizable/css/styles.css';
import { Responsive, WidthProvider } from "react-grid-layout";

const ResponsiveGridLayout = WidthProvider(Responsive);
const Dashboard = () => {



  return (
    <div >
      <h1>Dashboard</h1>

      <div key="section1" style={{ border: '2px solid #0000FF', backgroundColor: '#b8a8a7' }}>
        <ResponsiveGridLayout

          layout={{
            lg: [
              { i: 'widget1', x: 0, y: 0, w: 3, h: 2, minW: 3, minH: 2, maxW: 3, maxH: 2 },
              { i: 'widget2', x: 3, y: 0, w: 3, h: 2, minW: 3, minH: 2, maxW: 3, maxH: 2 },
              { i: 'widget3', x: 6, y: 0, w: 6, h: 6, minW: 6, minH: 6, maxW: 6, maxH: 6 },

              { i: 'widget4', x: 0, y: 3, w: 3, h: 2, minW: 3, minH: 2, maxW: 3, maxH: 2 },
              { i: 'widget5', x: 3, y: 3, w: 3, h: 2, minW: 3, minH: 2, maxW: 3, maxH: 2 },
              { i: 'widget6', x: 0, y: 6, w: 3, h: 2, minW: 3, minH: 2, maxW: 3, maxH: 2 },


              { i: 'widget7', x: 3, y: 6, w: 3, h: 2, minW: 3, minH: 2, maxW: 3, maxH: 2 },
            ]
          }}

          breakpoints={{ lg: 1200}}
          cols={{ lg: 12}}
         
          rowHeight={30}
          items={7}
        // allowOverlap={false}
        // isDroppable={false}
        >
          {['widget1', 'widget2', 'widget3', 'widget4', 'widget5', 'widget6', 'widget7'].map((widgetKey, index) => (
            <div key={widgetKey} style={{ border: '2px solid #ccc' }}>
              <h3>{`Widget ${index + 1}`}</h3>
              <p>{`Content for Widget ${index + 1}`}</p>
            </div>
          ))}
          {/* {creating reusable com} */}
        </ResponsiveGridLayout>
      </div>


    </div>
  );
};

export default Dashboard;
